import React from 'react';
export default class FilesList extends React.Component{
    getNames(files){
        let names=[];
        for(let i=0;i<files.length;i++){
            names.push(files[i].name);
        }
        return names;
    }
    getSize(file){
        let size=file.size;
        if(size<1000000){
            return `${Math.round(size/1000)} kb`;
        }
        else{
            size=size/1000000;
            return `${Math.floor(size * 100) / 100} mb`;
        }

    }
    getName(file){
        return file.name;
        }
    getFilesInformation(files){
        let filesInfo=[];
        for(let i=0;i<files.length;i++){
            filesInfo.push(this.getName(files[i])+" "+this.getSize(files[i]));
        }
        return filesInfo;
    }
    render(){
        let files=this.getFilesInformation(this.props.files);
        return(
            <div>{files}</div>
        )
    }
}
