import './DragAndDrop.css';
export default class DragAndDrop extends React.Component{
    constructor(props){
        super(props);
        this.state={
            files:[],
        }
    }
    dragOver(e){
        e.preventDefault();
    }
    dragEnter(e){
        e.preventDefault();
    }
    dragLeave(e){
        e.preventDefault();
    }
    fileDrop(e){
        e.preventDefault();
        const files=e.dataTransfer.files;
        this.setState({files:files});
    }
